import React, { useState } from "react";
import { Outlet, Link, useNavigate } from "react-router-dom";
import Alert from "./Components/Alert";


function App() {
  const [jwtToken, setJwtToken] = useState("")
  const [alertMessage, setAlertMessage] = useState("")
  const [alertClassName, setAlertClassName] = useState("d-none")
  const navigate = useNavigate()
  function logout() {
    setJwtToken("")
    navigate("/login")
  }
  useEffect(() => {
    if (jwtToken === "") {
      const requestOptions = {
        method: "GET",
        credentials: "include",
      }
      fetch('/refresh', requestOptions)
        .then((response) => response.json())
        .then((data) => {
          if (data.access_token) {
            setJwtToken(data.access_token)
          }
        })
        .catch(error => {
          console.log("user is not logged in", error)
        })
    }
  }, []);

  return (
    <div className="container">
      <div className="row">
        <div className="col">
        <h1 className="mt-3">Go Watch a Movie!</h1>
        </div>
        <div className="col text-end">
          {jwtToken === "" ?
          <Link to="/login"><span className="badge bg-success">Log in</span></Link>
            : <a href="#!" onClick={logout}><span className="badge bg-danger">Log out</span></a>
          }
        </div>
        <hr className="mb-3"></hr>
      </div>
      <div className="row">
        <div className="col-md-2">
          <nav>
            <div className="list-group">
              <Link to="/" className="list-group-item list-group-item-action">Home</Link>
              <Link to="/movies" className="list-group-item list-group-item-action">Movies</Link>
              <Link to="/genres" className="list-group-item list-group-item-action">Genres</Link>
              {jwtToken !== "" &&
              <>
                <Link to="/admin/movie/0" className="list-group-item list-group-item-action">Add Movies</Link>
                <Link to="/manage-catalogue" className="list-group-item list-group-item-action">Manage Catalogue</Link>
                <Link to="/graphql" className="list-group-item list-group-item-action">GraphQL</Link>
              </>
              }
            </div>
          </nav>
        </div>
        <div className="col-md-10">
          <Alert message={alertMessage} className={alertClassName} />
          <Outlet context={{
             jwtToken, setJwtToken, setAlertClassName, setAlertMessage,
           }}/>
        </div>
      </div>
    </div>


  );
}

export default App;
